import { useNavigate } from 'react-router-dom';
import { useState, useEffect, React } from "react";
import axios from 'axios';
import './login.css'
function Login() {
    // React States
    const [errorMessages, setErrorMessages] = useState({});
    const [data, setData] = useState([]);
    const [isSubmitted, setIsSubmitted] = useState(false);
    useEffect(() => {
        axios
            .get("http://localhost:5001/fetchUser")
            // .get("https://flora-host.herokuapp.com/fetchUser")
            // .get("https://food-ordering-server1.onrender.com/fetchUser")
            .then((res) => setData(res.data))
            .catch((err) => console.log(err, "it has an error"));
    });
    // User Login info
    const navigate = useNavigate();

    const errors = {
        uname: "invalid username",
        pass: "invalid password"
    };

    const handleSubmit = (event) => {
        //Prevent page reload
        event.preventDefault();

        var { uname, pass } = document.forms[0];

        // Find user login info
        const userData = data.find((user) => user.uname === uname.value);

        // Compare user info
        if (userData) {
            if (userData.password !== pass.value) {
                // Invalid password
                setErrorMessages({ name: "pass", message: errors.pass });
            } else {
                setIsSubmitted(true);
            }
        } else {
            // Username not found
            setErrorMessages({ name: "uname", message: errors.uname });
        }
    };

    // Generate JSX code for error message
    const renderErrorMessage = (name) =>
        name === errorMessages.name && (
            <div className="error">{errorMessages.message}</div>
        );

    // JSX code for login form
    const renderForm = (
        <div className="form">
            <form onSubmit={handleSubmit}>
                <div className="input-container">
                    <label>Username </label>
                    <input type="text" name="uname" required />
                    {renderErrorMessage("uname")}
                </div>
                <div className="input-container">
                    <label>Password </label>
                    <input type="password" name="pass" required />
                    {renderErrorMessage("pass")}
                </div>
                <div className="button-container">
                    <input type="submit" />
                </div>
            </form>
            {/* <div>
        {data.map((items)=>
            {
                return<>
                <div>
                    <p>Username = {items.username}</p>
                    <p>Password = {items.password}</p>
                </div>
                </>

            })
        }
      </div> */}
        </div>
    );

    return (
        <div className="app">
            <div className="login-form">
                {/* <div className="title">Sign In</div> */}
                {isSubmitted ? navigate('/FoodDetailsForm') : renderForm}
            </div>
        </div>
    );
}

export default Login;
